mapper.select:
select *
from tbl_prod_goods_stock_wave
where goods_id = #{goodsId}
and state != 'X'
and stock_in_date = #{req.stockInDate}

mapper.update:
update tbl_prod_goods_stock_wave
set state = 'X', state_date = now(), modify_by = #{updateStaffId}, modify_time = now()
where stock_wave_id = #{stockWaveId}

update tbl_prod_goods_stock_wave
stock_in_type = #{req.stockInType}, modify_by = #{req.updateStaffId}, modify_time = now(),
stock_in_date = #{req.stockInDate},
qty = #{req.qty},
comments = #{req.comments}
where stock_wave_id = #{stockWaveId}
