mapper.select:
select * from tbl_prod_goods_rel
where state = 'A'
and rel_type = #{relType}
and (
        ( 
            a_goods_id in

                #{item}

            and z_goods_id = #{id}
        )
        OR 
        (
            a_goods_id = #{id}
            and z_goods_id in

                #{item}

        ) 
    )

mapper.update:
update tbl_prod_goods_rel
set state = 'X', modify_time = now(), modify_by = #{modifyBy}
where rel_id in

    #{item.relId}

update tbl_prod_goods_rel
set state = 'X', modify_time = now(), modify_by = #{modifyBy}
where a_goods_id = #{aGoodsId}

update tbl_prod_goods_rel
set state = 'X', modify_time = now(), modify_by = #{modifyBy}
where a_goods_id = #{aGoodsId}
and rel_type = #{relType}

mapper.select:
select * 
from tbl_prod_goods
where goods_id in
(
    select z_goods_id 
    from tbl_prod_goods_rel
    where state = 'A'
    and a_goods_id = #{goodsRel.agoodsId}
    and rel_type = #{goodsRel.relType}
)
